function init() {/* Registrar services Worker */if ('serviceWorker' in navigator) {navigator.serviceWorker.register('sw.js').then(() => console.log('Service Worker registrado')).catch(err => console.error('Error:', err));}/*Constantes del menú principal*/const hamburguer = document.querySelector('.hamburger');const cerrarMenu = document.querySelector('.close');const menu = document.querySelector('.menu-navegacion');const overlay = document.getElementById('overlay');const body = document.body;/* Evento click oara el menú principal*/hamburguer.addEventListener('click', () => {menu.classList.add('abierto');overlay.style.display = 'block';body.classList.add('no-scroll');});cerrarMenu.addEventListener('click', ()=> {menu.classList.remove('abierto');overlay.style.display = 'none';body.classList.remove('no-scroll');});/*overlay.addEventListener('click', cerrarMenu); *//* Evento click con condicional para los menú de navegación principal*/window.addEventListener('click', (e) => {if (menu.classList.contains('abierto') && e.target != menu &&e.target != hamburguer) {menu.classList.toggle('abierto');overlay.style.display = 'none';body.classList.remove('no-scroll');}});/*Set current year*/document.getElementById('year').textContent = new Date().getFullYear();/*Form submission*/const budgetForm = document.getElementById('budgetForm');budgetForm.addEventListener('submit', function(e) {e.preventDefault();/*Get form data*/const formData = new FormData(budgetForm);const name = budgetForm.querySelector('input[type="text"]').value;const email = budgetForm.querySelector('input[type="email"]').value;const service = budgetForm.querySelector('select').value;const message = budgetForm.querySelector('textarea').value;/*Create WhatsApp message*/const whatsappMessage = `Hola, estoy interesado en sus servicios. Mi nombre es ${name} (${email}). Me interesa el servicio de ${service}. Mensaje: ${message}`;const encodedMessage = encodeURIComponent(whatsappMessage);/*Open WhatsApp*/window.open(`https://wa.me/584120348988?text=${encodedMessage}`, '_blank');/* Reset form*/budgetForm.reset();/* Show confirmation*/alert('¡Gracias por tu mensaje! Serás redirigido a WhatsApp para completar tu consulta.');});}window.onload = init();